INSERT INTO `pages` (`id`, `title`, `slug`, `content`, `meta_title`, `meta_description`, `keywords`, `meta_image`, `created_at`, `updated_at`) VALUES
(1, 'Custom Page', 'custom-page', '<p><span style=\"color: rgba(255, 255, 255, 0.8); font-family: &quot;Open Sans&quot;, sans-serif; font-size: 13px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(38, 38, 47); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\">salebazarbd.com is a Multi vendor e-commerce website/Marketplace &amp; online platform that brings together several buyers and sellers.</span></p>', 'Custom Page', 'Custom Page', 'Custom Page', NULL, '2021-05-07 19:36:18', '2023-03-25 12:49:46'),
(2, 'Cancellation Policy', 'cancellation-policy', '<p><strong>বাতিলকরণ নীতি:</strong> </p><p>অর্ডার কনফার্মেশনের পরেও অনিবার্য কারনবশত যেকোনো সময়ে salebazarbd.com আপনার অর্ডার বাতিল করার ক্ষমতা রাখে।</p><p>পণ্য স্টক এ না থাকার কারণে, অর্ডার করা পণ্যের মূল্যে কোনো সমস্যা থাকলে, অর্ডার এ কোনো সমস্যা থাকলে, অথবা অন্য কোনো কারণে যেকোনো অর্ডার প্রত্যাখ্যান/&nbsp;বাতিল করার আমাদের অধিকার রয়েছে।</p><p>অ্যাডভান্স  (কার্ড / বিকাশ/ রকেট/ নগদ) অনলাইন পেমেন্ট এর ক্ষেত্রে পণ্য স্টকে না থাকলে অথবা ক্রেতা নিতে ইচ্ছুক না হলে অথবা বিক্রেতা ডেলিভারি করতে ব্যর্থ হলে অর্ডার ক্যানসেল করে যে নাম্বার থেকে অ্যাডভান্স পেমেন্ট করা হয়েছে , সেই নাম্বারেই ৭ কার্যদিবসের মধ্যে রিফান্ড করা হবে।</p>', 'Cancellation Policy', 'Cancellation Policy', 'Cancellation policy', NULL, '2023-04-30 14:33:59', '2023-05-05 16:03:39'),
(3, 'Refund Policy', 'Refund-Policy', '<p>রিফান্ড&nbsp;নীতি:</p><p>অ্যাডভান্স (কার্ড / বিকাশ/ রকেট/ নগদ) অনলাইন পেমেন্ট এর ক্ষেত্রে পণ্য স্টকে না থাকলে অথবা ক্রেতা নিতে ইচ্ছুক না হলে অথবা বিক্রেতা ডেলিভারি করতে ব্যর্থ হলে অর্ডার ক্যানসেল করে যে নাম্বার থেকে অ্যাডভান্স পেমেন্ট করা হয়েছে , সেই নাম্বারেই ৭ কার্যদিবসের মধ্যে রিফান্ড করা হবে।</p><p>*যদি প্রোডাক্ট রিসিভ করার সময় ভাঙ্গা বা ডিফেক্টিভ থাকে।*&nbsp;যদি কোন আনুসাঙ্গিক পার্ট মিসিং থাকে।* যদি Salebazarbd.com এ উল্লেখিত বর্ণনার সাথে মিল না থাকে।এক্ষেত্রে&nbsp;প্রোডাক্ট এর অবস্থা যাচাইকরে ৭ কর্মদিবসের মধ্যে Salebazarbd.com অথবা সেলার রিটার্ন কৃত প্রোডাক্ট এর মূল্য ফেরত দিবে অথবা পরিবর্তন করে দিবে।</p><p>* যদি ক্রেতার কাছে থাকা অবস্থায় প্রোডাক্ট ভাঙ্গা বা ডিফেক্টিভ প্রমাণিত হয়।*. যদি Salebazarbd.com এ উল্লেখিত বর্ণনার সাথে মিল থাকে, কিন্তু আপনি ভূল পণ্য অর্ডার করে থাকেন।* যদি রিটার্নকৃত প্রোডাক্ট এর বক্স, ওয়ারেন্টিকার্ড ও আনুসাঙ্গিক পার্ট অক্ষত না থাকে।এক্ষেত্রে প্রোডাক্ট এর অবস্থা যাচাই করে রিটার্ন / রিটার্নকৃত প্রোডাক্ট এর মূল্য ফেরত /পরিবর্তন করা হবেনা।</p>', 'Refund Policy', 'Refund Policy', 'Refund Policy', NULL, '2023-05-02 06:07:31', '2023-05-02 06:17:10'),
(4, 'Seller Benefits', 'Seller-Benefits', '<p><strong>salebazarbd.com এ কেন বিক্রেতা হবেন? কি কি সুবিধা পাবেন?</strong></p><p style=\"text-align: justify;\">1. Seller  Panel. 2. Seller Web page with shop Link. 3. Live Chat (Customer &amp; seller) 3. POS Manager. 4. Profit &amp; Loss panel. 5. Seller Can Publish &amp; Unpublish Product. 6. Support Ticket system. 7. Automatic create Invoice. 8. Automatic order invoice mail system. 9. Notification option. 10. Refund system. 11. Sellers own/ prefer courier for Product delivery.</p><p>salebazarbd.com এর  বিক্রেতা হয়ে উঠুন:&nbsp;</p><p>Click-&nbsp;<a href=\"https://salebazarbd.com/shops/create\">Seller Registration form link (Be a seller)</a></p>', 'Seller Benefits', 'Seller Benefits', 'Seller Benefits', 'uploads/custom-pages/Y4iZFSnhFHgZsN0qSXhGHu0B1R77HjYibjhEMMzX.png', '2023-08-04 18:18:11', '2023-08-05 12:11:35');
